import React, {useState} from "react";

import "./ExpenseForm.css";

const ExpenseForm = () => {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredDate, setEnteredDate] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");


  const titleChangehandler = (event) =>{
   setEnteredTitle(event.target.value);
  };

const amountChangeHandler = (event) =>{
  setEnteredAmount(event.target.value)
}
const dateChangeHandler = (event) =>{
  setEnteredDate(event.target.value)
}

const submitHandler = (event)=>{
event.preventDefault();
const expenseData ={
  title:enteredTitle,
  amount:enteredAmount,
  date: new Date(enteredDate)
};
console.log(expenseData);
setEnteredTitle('');
setEnteredTitle('');
setEnteredTitle('');
}; 
  return (
    <form onSubmit={submitHandler}> 
      <div className="new-expense_controls">
        <div className="new-expense_control">
          <label>Title</label>
          <input type="text" onClick={titleChangehandler} value={enteredTitle}/>
        </div>  
         <div className="new-expense_control">
          <label>Amount</label>
          <input type="num" min="0.01" step="0.01" onClick={amountChangeHandler} value={enteredAmount}/>
        </div>
        <div className="new-expense_control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onClick ={dateChangeHandler} value={enteredDate}/>
        </div>
      </div>
      <div className="new-expense_actions"><button type="submit">Add Expense</button></div>
    </form>
  );
};export default ExpenseForm;
